import React, {Component} from 'react'
import Web3 from 'web3'
import Navbar from './Navbar.js'
import NewbieToken from '../truffle_abis/NewbieToken.json'

class App extends Component {

    async UNSAFE_componentWillMount(){
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3() {
        if(window.ethereum){
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }else{
            window.alert('You dont have metamask')
        }
    }

    async loadBlockchainData(){
        const web3 = window.web3
        const account = await web3.eth.getAccounts()
        this.setState({account: account[0]})
        const networkId = await web3.eth.net.getId()
        
        const newbData = NewbieToken.networks[networkId]
        if(newbData){
            const newb = web3.eth.Contract(NewbieToken.abi, newbData.address)
            this.setState({newb})
            let newbBalance = newb.methods.balanceOf(this.state.account).call()
            this.setState({newbBalance: newbBalance.toString()})
            console.log({balance: newbBalance})
        }else {
            window.alert('Error! NewbContract not deployed')
        }
    }

    constructor(props){
        super(props)
        this.state = {
            account: '0x0',
            newb: {},
            rwd: {},
            decentralBank: {},
            newbBalance: '0',
            rwdBalance: '0',
            stakingBalance: '0',
            loadin: true
        }
    }

    render(){
        return (
            <div>
                <Navbar account={this.state.account}/>
                <div className='text-center'>
                        
                </div>
            </div>

        )        
    }
}

export default App;
