import React, {Component} from "react"

class Airdrop extends Component{

    constructor(){
        super();
        this.state = {time: {}, seconds: 20};
        this.timer = 0;
        this.startTimer = this.startTimer.bind(this); 
        this.countDown = this.countDown.bind(this); 
    }

    startTimer(){
        if(this.timer == 0){
            this.timer = setInterval(this.countDown, 1000)

        }
    }

    countDown(){
        let seconds = this.state.seconds - 1
        this.setState({
            time: this.secondsToTime(seconds),
            seconds: seconds
        })
        if (seconds == 0){
            clearInterval(this.timer)
        }
    }

    secondsToTime(secs){
        let hours, seconds, minutes
        hours = Math.floor(secs / (60 * 60))

        let devisor_for_minutes = secs % (60*60)
        minutes = Math.floor(devisor_for_minutes /60)

        let devisor_for_seconds = devisor_for_minutes % 60
        seconds = Math.ceil(devisor_for_minutes)

        let obj = {
            'h': hours,
            'm': minutes,
            's': seconds
        }

        return obj
    }

    componentDidMount(){
        let timeLeftVar = this.secondsToTime(this.state.seconds)
        this.setState({time: timeLeftVar})
    }



    render(){
        return (
            <div style={{color:'black'}}>{this.state.time.m}:{this.state.time.s}
                {this.startTimer()}
            </div>
        )
    }
}

export default Airdrop;
